mapper.insert:
/* user-mapper.xml insertCustomerUser */ INSERT INTO tbcs_customer_mobile ( cust_id /* VARCHAR(32) '사용자 ID(UUID)' */
, cust_nm /* VARCHAR(50) '사용자 명' */
, ssn
, cust_cell
, mo_cd
, approved_yn
, data_dcd
, in_emp_cd , up_emp_cd , in_dtm , up_dtm ) VALUES ( #{cust_id} /* VARCHAR(32) '사용자 ID(UUID)' */
, #{cust_nm} /* VARCHAR(50) '사용자 명' */
, #{ssn}
, #{cust_cell}
, #{mo_cd}
, #{approved_yn}
, #{data_dcd}
, 'ADMIN' , 'ADMIN' , SYSDATE , SYSDATE )

mapper.update:
/* user-mapper.xml updateCustomerUser */
UPDATE tbcs_customer_mobile 
   SET up_dtm  = SYSDATE     		/* DATETIME        '등록 일시' */
	 
	 , cust_nm = #{cust_nm}       /* VARCHAR(50)     '사용자 명' */
	 
	 
	 , ssn = #{ssn}
	 
	 
	 , cust_cell = #{cust_cell}
	 
	 
	 , mo_cd = #{mo_cd}
	 
	 
	 , approved_yn = #{approved_yn}
	 
	 
	 , data_dcd = #{data_dcd}
	 
 WHERE cust_id = #{cust_id}

mapper.delete:
/*  user-mapper.xml deleteCustomerUser */
DELETE tbcs_customer_mobile
 WHERE cust_id = #{cust_id}

mapper.select:
SELECT tb2.user_id /* VARCHAR(100) '사용자 ID' */ , tb1.login_id /* VARCHAR(100) '로그인 ID' */ , tb2.cust_nm /* VARCHAR(50) '사용자 이름' */ , tb1.login_pw /* VARCHAR(256) '로그인 비밀번호' */ , tb2.cust_cell /* VARCHAR(256) '사용자 ID' */ FROM tbcs_mobile_login tb1 JOIN tbcs_customer_mobile tb2 ON tb1.user_id = tb2.user_id WHERE 1 = 1
AND tb1.login_id = #{login_id}
AND tb3.contact_no = #{contact_no}

/* user-mapper.xml getCustomerLoginInfoList */


	AND tb1.login_id = #{login_id}


	AND tb1.cust_id = #{cust_id}

/* user-mapper.xml getCustomerLoginInfo */


	AND tb1.login_id = #{login_id}


	AND tb1.cust_id = #{cust_id}

mapper.delete:
/* user-mapper.xml deleteCustomerLoginInfo */
DELETE tbcs_mobile_login
 WHERE login_id = #{login_id}

mapper.select:
/* user-mapper.xml getUserDeviceList */ SELECT SEQ as seq , DEVC_ID as devc_id , OS_NAME as os_name , OS_VER as os_ver , DEVC_NAME as devc_name , APP_VER as app_ver , DEVC_TOKEN as devc_token , USER_ID as user_id , IN_USER_ID as in_user_id , IN_DTM as in_dtm FROM TBSY_APP_CONN_LOG tb1
AND tb1.USER_ID = #{user_id}
ORDER BY IN_DTM DESC

mapper.insert:
/* user-mapper.xml insertUserDevice */
INSERT 
  INTO TBSY_APP_CONN_LOG
     ( SEQ 
     , DEVC_ID
     , OS_NAME
     , OS_VER
     , DEVC_NAME
     , APP_VER
     , DEVC_TOKEN
     , USER_ID
     , IN_USER_ID
     , IN_DTM
     ) 
VALUES
     ( TBSY_APP_CONN_LOG_SEQ.NEXTVAL 
     , #{devc_id}
     , #{os_name}
     , #{os_ver}
     , #{devc_name}
     , #{app_ver}
     , #{devc_token}
     , #{user_id}
     , #{in_user_id}
     , SYSDATE
     )

mapper.update:
/* user-mapper.xml insertUserDevice */
UPDATE TBSY_APP_CONN_LOG
   SET DEVC_ID 		= #{devc_id}
     , OS_NAME 		= #{os_name}
     , OS_VER 		= #{os_ver}
     , DEVC_NAME 	= #{devc_name}
     , APP_VER 		= #{app_ver}
     , DEVC_TOKEN 	= #{devc_token} 
     , USER_ID		= #{user_id}
     , IN_USER_ID   = #{in_user_id}
     , IN_DTM       = SYSDATE
 WHERE SEQ = #{seq}

mapper.delete:
/* user-mapper.xml insertUserDevice */
DELETE FROM TBSY_APP_CONN_LOG
 WHERE SEQ = #{seq}
